_IMAGE_PNG_B64 = """"""
_IMAGE_ICO_B64 = """"""

def _load_image_from_b64(b64str):
    if not b64str:
        img = Image.new('RGBA', (64, 64), (100, 100, 100, 255))
        return img
    data = base64.b64decode(b64str)
    return Image.open(io.BytesIO(data)).convert("RGBA")

def _write_temp_ico(b64str):
    if not b64str:
        return None
    try:
        data = base64.b64decode(b64str)
        fd, path = tempfile.mkstemp(suffix=".ico")
        os.close(fd)
        with open(path, "wb") as f:
            f.write(data)
        return path
    except Exception:
        return None

def key_to_str(key):
    if isinstance(key, KeyCode):
        ch = key.char
        return ch.lower() if ch else None
    
    if hasattr(key, 'name'):
        name = key.name.lower()
        if name in ('ctrl_l', 'ctrl_r'):
            return 'ctrl'
        if name in ('alt_l', 'alt_r'):
            return 'alt'
        if name in ('shift_l', 'shift_r'):
            return 'shift'
        if name in ('cmd_l', 'cmd_r'):
            return 'cmd'
        return name
    
    return None

def token_to_str(token: str):
    if not token:
        return None
    t = token.strip().lower()
    
    synonyms = {
        'control': 'ctrl',
        'command': 'cmd',
        'meta': 'cmd',
        'win': 'cmd',
        'super': 'cmd',
        'caps': 'caps_lock',
        'capslock': 'caps_lock',
        'ins': 'insert',
        'pageup': 'page_up',
        'pagedown': 'page_down',
    }
    
    if t in synonyms:
        return synonyms[t]
    
    if t.startswith('f') and t[1:].isdigit():
        return t
    
    if len(t) == 1:
        return t
    
    return t

def parse_hotkey_to_set(hotkey_str: str):
    parts = [p.strip() for p in hotkey_str.strip().split('+') if p.strip()]
    normalized = set()
    for p in parts:
        s = token_to_str(p)
        if s:
            normalized.add(s)
    return frozenset(normalized)

class HotkeyWatcher:
    def __init__(self):
        self._current_keys = set()
        self._hotkey_combo = set()
        self._callback = None
        self._listener = None
        self._lock = threading.Lock()
        self._triggered = False
    
    def set_hotkey(self, hotkey_str: str, callback):
        with self._lock:
            self._hotkey_combo = parse_hotkey_to_set(hotkey_str)
            self._callback = callback
    
    def start(self):
        if self._listener:
            return
        self._listener = keyboard.Listener(
            on_press=self._on_press,
            on_release=self._on_release
        )
        self._listener.start()
    
    def stop(self):
        if self._listener:
            try:
                self._listener.stop()
            except:
                pass
            self._listener = None
        with self._lock:
            self._current_keys.clear()
            self._triggered = False
    
    def _on_press(self, key):
        key_str = key_to_str(key)
        if not key_str:
            return
        
        with self._lock:
            self._current_keys.add(key_str)
            
            if self._hotkey_combo and self._current_keys == self._hotkey_combo:
                if not self._triggered and self._callback:
                    self._triggered = True
                    try:
                        self._callback()
                    except:
                        traceback.print_exc()
    
    def _on_release(self, key):
        key_str = key_to_str(key)
        if not key_str:
            return
        
        with self._lock:
            if key_str in self._current_keys:
                self._current_keys.discard(key_str)

            if self._triggered and not self._current_keys.intersection(self._hotkey_combo):
                self._triggered = False

BUTTON_MAP = {
    'left': mouse.Button.left,
    'middle': mouse.Button.middle,
    'right': mouse.Button.right
}

def pretty_name_for(token: str) -> str:
    lp = token.lower()
    
    if lp in ('ctrl', 'control'):
        return 'Ctrl'
    if lp == 'alt':
        return 'Alt'
    if lp == 'shift':
        return 'Shift'
    if lp in ('cmd', 'super', 'win', 'meta'):
        return 'Cmd'
    
    if lp in ('capslock', 'caps_lock'):
        return 'CapsLock'
    if lp in ('num_lock', 'numlock'):
        return 'NumLock'
    if lp in ('scroll_lock', 'scrolllock'):
        return 'ScrollLock'
    
    nav_map = {
        'page_up': 'PageUp',
        'page_down': 'PageDown',
        'insert': 'Ins',
        'delete': 'Del',
        'home': 'Home',
        'end': 'End',
        'up': 'Up',
        'down': 'Down',
        'left': 'Left',
        'right': 'Right',
    }
    if lp in nav_map:
        return nav_map[lp]
    
    if lp.startswith('f') and lp[1:].isdigit():
        return lp.upper()
    
    if len(lp) == 1:
        return lp.upper()
    
    return lp.capitalize()

class AutoClickerApp:
    def __init__(self, root):
        self.root = root
        self._is_hiding_to_tray = False # Flag to prevent minimize recursion
        
        try:
            default_font = tkfont.nametofont("TkDefaultFont")
            default_font.configure(family='Segoe UI', size=10)
            text_font = tkfont.nametofont("TkTextFont")
            text_font.configure(family='Segoe UI', size=10)
            
            style = ttk.Style()
            style.configure(".", font=("Segoe UI", 10))
            style.configure("TButton", padding=(8, 6))
            style.configure("TEntry", padding=(4, 4))
            
            self.hdr_font = ("Segoe UI", 16, "bold")
        except Exception:
            self.hdr_font = ("TkDefaultFont", 14, "bold")
            
        root.title("asterxsk's autoclicker")
        root.protocol("WM_DELETE_WINDOW", self._clean_exit)
        root.bind("<Unmap>", self._on_minimize)
        
        self.mouse_controller = mouse.Controller()
        self.clicking = threading.Event()
        self.stop_program = threading.Event()
        self.click_thread = None
        self.hotkey_watcher = None
        
        self.interval_var = StringVar(value='100')
        self.button_var = StringVar(value='left')
        self.hotkey_var = StringVar(value='capslock')
        self.count_var = StringVar(value='')
        self.status_var = StringVar(value='Status: Idle')
        self.hotkey_display_var = StringVar()
        
        self.orig_img = _load_image_from_b64(_IMAGE_PNG_B64)
        
        try:
            tmp_ico = _write_temp_ico(_IMAGE_ICO_B64)
            if tmp_ico and sys.platform.startswith('win'):
                try:
                    root.iconbitmap(tmp_ico)
                except:
                    pass
        except Exception:
            pass
        
        self._build_ui()
        self._auto_size_window()
        
        self.register_hotkey()
        
        if self.hotkey_watcher:
            self.hotkey_watcher.start()
        
        self._create_tray_icon(self.orig_img)
    
    def _on_minimize(self, event):
        if self._is_hiding_to_tray:
            return

        if self.root.wm_state() == 'iconic':
            self.hide_to_tray()
    
    def _auto_size_window(self, padding_w=40, padding_h=60, max_width_ratio=0.95, max_height_ratio=0.9):
        self.root.update_idletasks()
        
        req_w = self.root.winfo_reqwidth() + padding_w
        req_h = self.root.winfo_reqheight() + padding_h
        
        sw = self.root.winfo_screenwidth()
        sh = self.root.winfo_screenheight()
        
        max_w = int(sw * max_width_ratio)
        max_h = int(sh * max_height_ratio)
        
        final_w = min(req_w, max_w)
        final_h = min(req_h, max_h)
        
        final_w = max(final_w, 450)
        final_h = max(final_h, 300)
        
        x = max(0, (sw - final_w) // 2)
        y = max(0, (sh - final_h) // 2 - 20)
        
        self.root.geometry(f"{final_w}x{final_h}+{x}+{y}")
        self.root.minsize(400, 250)
    
    def _build_ui(self):
        pad = {'padx': 12, 'pady': 10}
        
        frm = ttk.Frame(self.root, padding=12)
        frm.pack(fill='both', expand=True)
        
        hdr = ttk.Label(frm, text="asterxsk's autoclicker", font=self.hdr_font)
        hdr.grid(row=0, column=0, columnspan=3, sticky='w', **pad)
        
        ttk.Label(frm, text='Interval (ms):').grid(row=1, column=0, sticky='e', **pad)
        ttk.Entry(frm, textvariable=self.interval_var, width=15).grid(row=1, column=1, sticky='w', **pad)
        
        ttk.Label(frm, text='Mouse button:').grid(row=2, column=0, sticky='e', **pad)
        combo = ttk.Combobox(frm, textvariable=self.button_var, 
                             values=['left', 'middle', 'right'], width=13)
        combo.grid(row=2, column=1, sticky='w', **pad)
        combo['state'] = 'readonly'
        
        ttk.Label(frm, text='Hotkey (click to record):').grid(row=3, column=0, sticky='e', **pad)
        
        self.hotkey_display_label = ttk.Label(frm, textvariable=self.hotkey_display_var, font=("Segoe UI", 11, "bold"), cursor="hand2")
        self.hotkey_display_label.grid(row=3, column=1, sticky='w', padx=pad['padx'], pady=pad['pady'])
        self.hotkey_display_label.bind('<Button-1>', lambda e: self._record_hotkey())
        
        self._render_hotkey_widget()
        
        ttk.Label(frm, text='Click count (optional):').grid(row=4, column=0, sticky='e', **pad)
        ttk.Entry(frm, textvariable=self.count_var, width=15).grid(row=4, column=1, sticky='w', **pad)
        
        btn_frame = ttk.Frame(frm)
        btn_frame.grid(row=5, column=0, columnspan=3, sticky='w', **pad)
        
        ttk.Button(btn_frame, text='Start', command=self.start_clicking).pack(side='left', padx=4)
        ttk.Button(btn_frame, text='Stop', command=self.stop_clicking).pack(side='left', padx=4)
        
        status = ttk.Label(self.root, textvariable=self.status_var, relief='sunken', anchor='w')
        status.pack(side='bottom', fill='x')
        
        frm.grid_columnconfigure(0, weight=0)
        frm.grid_columnconfigure(1, weight=0)
        frm.grid_columnconfigure(2, weight=1)
    
    def _render_hotkey_widget(self):
        hk = self.hotkey_var.get().strip()
        if not hk:
            self.hotkey_display_var.set("Click to set hotkey")
        else:
            parts = [p.strip() for p in hk.split('+') if p.strip()]
            pretty_parts = [pretty_name_for(p) for p in parts]
            display_string = ' + '.join(pretty_parts)
            self.hotkey_display_var.set(display_string)

    def _create_tray_icon(self, pil_img):
        try:
            self.tray_icon = pystray.Icon(
                'asterxsk_autoclicker',
                pil_img,
                "asterxsk's autoclicker"
            )
            
            self.tray_icon.menu = pystray.Menu(
                pystray.MenuItem('Show', lambda: self.show_window()),
                pystray.MenuItem(
                    lambda text: 'Stop' if self.clicking.is_set() else 'Start',
                    lambda: self._tray_toggle()
                ),
                pystray.MenuItem('Exit', lambda: self._tray_exit())
            )
            
            t = threading.Thread(target=self.tray_icon.run, daemon=True)
            t.start()
        except Exception:
            traceback.print_exc()
    
    def show_window(self):
        try:
            self._is_hiding_to_tray = False
            self.root.deiconify()
            self.root.after(0, self.root.lift)
        except:
            pass
    
    def hide_to_tray(self):
        try:
            self._is_hiding_to_tray = True
            self.root.withdraw()
        finally:
            self._is_hiding_to_tray = False
    
    def _tray_toggle(self):
        if self.clicking.is_set():
            self.stop_clicking()
        else:
            self.start_clicking()
    
    def _tray_exit(self):
        self._clean_exit()
    
    def _record_hotkey(self):
        dlg = Toplevel(self.root)
        dlg.title('Record hotkey')
        
        self.root.update_idletasks()
        w = 400
        h = 150
        x = self.root.winfo_x() + (self.root.winfo_width() - w) // 2
        y = self.root.winfo_y() + (self.root.winfo_height() - h) // 2
        dlg.geometry(f'{w}x{h}+{x}+{y}')
        
        dlg.resizable(False, False)
        dlg.transient(self.root)
        dlg.grab_set()
        
        Label(dlg, text='Press the key combination you want,\nthen press Enter to accept or Esc to cancel.',
              font=('Segoe UI', 10)).pack(padx=20, pady=15)
        
        display = StringVar(value='Waiting for input...')
        disp_lbl = Label(dlg, textvariable=display, font=('Segoe UI', 12, 'bold'), fg='#0066cc')
        disp_lbl.pack(padx=20, pady=10)
        
        pressed = set()
        captured = []
        stop_flag = threading.Event()
        
        def on_press(k):
            if k in (Key.enter, Key.esc):
                return
            
            kk = key_to_str(k)
            if kk is None:
                return
            
            pressed.add(kk)
            
            parts = sorted(list(pressed))
            combo = '+'.join([pretty_name_for(p) for p in parts])
            display.set(combo)
        
        def on_release(k):
            if k == Key.enter:
                combo = display.get().strip()
                if combo and combo != 'Waiting for input...':
                    parts = [p.strip() for p in combo.split('+')]
                    normalized = '+'.join([token_to_str(p.lower()) for p in parts if token_to_str(p.lower())])
                    captured.append(normalized)
                stop_flag.set()
                return False
            
            if k == Key.esc:
                stop_flag.set()
                return False

            kk = key_to_str(k)
            if kk:
                pressed.discard(kk)

            parts = sorted(list(pressed))
            combo = '+'.join([pretty_name_for(p) for p in parts])
            display.set(combo if combo else 'Waiting for input...')
        
        listener = keyboard.Listener(on_press=on_press, on_release=on_release)
        listener.start()
        
        def poll():
            if stop_flag.is_set():
                try:
                    listener.stop()
                except:
                    pass
                
                if captured:
                    newhk = captured[0]
                    self.hotkey_var.set(newhk)
                    try:
                        self._render_hotkey_widget()
                    except:
                        pass
                    self.register_hotkey()
                
                try:
                    dlg.destroy()
                except:
                    pass
            else:
                self.root.after(50, poll)
        
        self.root.after(50, poll)
        self.root.wait_window(dlg)
    
    def start_clicking(self):
        try:
            interval_ms = float(self.interval_var.get())
            if interval_ms <= 0:
                raise ValueError()
        except:
            messagebox.showerror('Invalid interval', 'Enter a positive number (milliseconds) for click interval.')
            return
        
        btn_text = self.button_var.get().strip().lower()
        btn = BUTTON_MAP.get(btn_text, mouse.Button.left)
        
        count = None
        if self.count_var.get().strip():
            try:
                count = int(self.count_var.get().strip())
                if count <= 0:
                    raise ValueError()
            except:
                messagebox.showerror('Invalid count', 'Click count must be a positive integer.')
                return
        
        self.interval_seconds = max(0.001, interval_ms / 1000.0)
        self.chosen_button = btn
        self.requested_count = count
        
        if self.click_thread and self.click_thread.is_alive():
            self.clicking.set()
            self.status_var.set('Status: Clicking')
            return
        
        self.clicking.set()
        self.stop_program.clear()
        self.click_thread = threading.Thread(target=self._click_loop, daemon=True)
        self.click_thread.start()
        self.status_var.set('Status: Clicking')
    
    def stop_clicking(self):
        self.clicking.clear()
        self.status_var.set('Status: Stopped')
    
    def _on_toggle_hotkey(self):
        if self.clicking.is_set():
            self.root.after(0, self.stop_clicking)
        else:
            self.root.after(0, self.start_clicking)
    
    def _click_loop(self):
        performed = 0
        
        try:
            while not self.stop_program.is_set():
                if self.clicking.is_set():
                    try:
                        self.mouse_controller.click(self.chosen_button, 1)
                    except:
                        pass
                    
                    performed += 1
                    
                    if self.requested_count is not None and performed >= self.requested_count:
                        self.clicking.clear()
                        self.root.after(0, lambda: self.status_var.set('Status: Finished'))
                        break
                    
                    sleep_target = self.interval_seconds
                    elapsed = 0.0
                    step = 0.01
                    
                    while elapsed < sleep_target and self.clicking.is_set() and not self.stop_program.is_set():
                        time.sleep(min(step, sleep_target - elapsed))
                        elapsed += step
                else:
                    time.sleep(0.05)
        
        except Exception:
            traceback.print_exc()
        finally:
            self.clicking.clear()
    
    def _clean_exit(self):
        self.stop_program.set()
        self.clicking.clear()
        
        try:
            if self.hotkey_watcher:
                self.hotkey_watcher.stop()
        except:
            pass
        
        try:
            self.tray_icon.stop()
        except:
            pass
        
        try:
            self.root.quit()
        except:
            pass
            
        os._exit(0)

    def register_hotkey(self):
        hk = self.hotkey_var.get().strip()
        
        try:
            if self.hotkey_watcher:
                self.hotkey_watcher.stop()
        except:
            pass
        
        self.hotkey_watcher = HotkeyWatcher()
        
        try:
            if hk:
                self.hotkey_watcher.set_hotkey(hk, self._on_toggle_hotkey)
            self.hotkey_watcher.start()
        except Exception:
            print('warning: hotkey registration failed')
            traceback.print_exc()

def main():
    root = Tk()
    
    try:
        root.tk.call('tk', 'scaling', 1.0)
    except:
        pass
    
    app = AutoClickerApp(root)
    root.mainloop()

if __name__ == '__main__':
    try:
        main()
    except KeyboardInterrupt:
        sys.exit(0)